import { Outlet } from "react-router-dom";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import logo from "../../images/Restify.jpg";


const SearchForm = () => {
    // create a state for query
    const [query, setQuery] = useState({ search: "", page_num: 1 });

    // create a state for properties
    const [properties, setProperties] = useState([]);

    // fetch the properties from the backend
    useEffect(() => {
        const { search, page_num } = query;
        fetch(`http://localhost:8000/property/list/?search=${search}&page=${page_num}`)
        .then(response => response.json())
        .then(data => {
            setProperties(data);
        });
    }, [query]);

    // make every three properties into a row
    const rows = [];
    for (let i = 0; i < properties.length; i += 3) {
        rows.push(properties.slice(i, i + 3));
    }

    // console.log("AAAAAA");
    console.log(rows);
    // var row = rows[0];
    // console.log(row?.[0].main_pic);

    const displayProperties = rows.map((row, index) => {
        console.log("AAAAAA");
        console.log(row?.[0].main_pic);

        var card_a = <></>;
        var card_b = <></>;
        var card_c = <></>;

        if (row?.[0]) {
            card_a = (
                <div className="column is-one-quarter box">
                    <Link>
                        <figure className="image is-5by4">
                            <img src={"http://127.0.0.1:8000/" + row?.[0].main_pic} alt="p_pic" style={{objectFit: "cover"}} />
                        </figure>
                    </Link>

                    <div>
                        <div>
                            {row?.[0].city}, {row[0].country}
                        </div>
                        <div>
                            {row?.[0].property_type}
                        </div>
                        <div>
                            {row?.[0].num_of_guests} guests
                        </div>
                        <div>
                            {row?.[0].lowest_avail_price}
                        </div>
                    </div>
                </div>
            );
        }

        if (row?.[1]) {
            card_b = (
                    
                <div className="column is-one-quarter box">
                    <Link>
                        <figure className="image is-5by4">
                            <img src={"http://127.0.0.1:8000/" + row?.[1].main_pic} alt="p_pic" style={{objectFit: "cover"}} />
                        </figure>
                    </Link>

                    <div>
                        <div>
                            {row?.[1].city}, {row[1].country}
                        </div>
                        <div>
                            {row?.[1].property_type}
                        </div>
                        <div>
                            {row?.[1].num_of_guests} guests
                        </div>
                        <div>
                            {row?.[1].lowest_avail_price}
                        </div>
                    </div>
                </div>

            );
        }

        if (row?.[2]) {
            card_c = (
                    
                <div className="column is-one-quarter box">
                    <Link>
                        <figure className="image is-5by4">
                            <img src={"http://127.0.0.1:8000/" + row?.[2].main_pic} alt="p_pic" style={{objectFit: "cover"}} />
                        </figure>
                    </Link>

                    <div>
                        <div>
                            {row[2].city}, {row[2].country}
                        </div>
                        <div>
                            {row[2].property_type}
                        </div>
                        <div>
                            {row[2].num_of_guests} guests
                        </div>
                        <div>
                            {row[2].lowest_avail_price}
                        </div>
                    </div>
                </div>

            );
        }


                
        return (
            <>

            <div className="columns">
                
                <div className="column">
    
                </div>

                    {card_a}
                    
                    <div className="column">
    
                    </div>

                    {card_b}
                    
                    <div className="column">
    
                    </div>
                    
                    {card_c}
                    
                    <div className="column">
    
                    </div>
            </div>
            </>
        );
    });

    return (
        <>
                <section className="hero is-small">
                    <div className="hero-body has-text-centered">
                        <div className="container">
                            <text style={{fontSize: 100 + 'px', fontFamily: "fantasy", color: "purple"}} > Restify </text>
                        </div>
                    </div>
                </section>

                <section className="hero is-small">
                <div className="hero-body has-text-centered">
                    <div className="container">

                        <div className="columns">
                            <div className="column"></div>

                            <div className="column is-three-fifths-desktop is-full-mobile is-three-fifths-tablet">
                                <input type="text" className="input"
                                    placeholder="Search for a property"
                                    value={query.search}
                                    onChange={(e) => setQuery({search: e.target.value})}
                                />
                            </div>

                            <div className="column"></div>
                        </div>

                    </div>

                </div>
                </section>

                <section className="section">
                    <div className="container">
                            
                            {displayProperties}

                    </div>

                </section>
        <Outlet />
        </>
    );
}

export default SearchForm;
