import Input from "../Input";
import { Outlet } from "react-router-dom";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";


const SearchForm = () => {
    // create a state for query
    const [query, setQuery] = useState({search: ""});

    // fetch the properties from the backend
    useEffect(() => {
        fetch("http://localhost:8000/property/list?search={query.search}")
        .then(response => response.json())
        .then(data => console.log(data))
    }, [query]);

    return (
        <>
                <section className="hero is-small">
                <div className="hero-body has-text-centered">
                    <div className="container">

                        <div className="columns">
                        <div className="column"></div>

                        <div className="column is-three-fifths-desktop is-full-mobile is-three-fifths-tablet">
                            <div className="field has-addons">

                            <div className="control">
                                <input
                                type="text"
                                placeholder="Search for a property"
                                value={query.search}
                                onChange={event => setQuery({search: event.target.value})}
                                />
                            </div>

                            {/* <div className="control">
                                <a className="button is-link">
                                Search
                                </a>
                            </div> */}

                            </div>
                        </div>

                        <div className="column"></div>
                        </div>

                    </div>

                </div>
                </section>
        <Outlet />
        </>
    );
}

export default SearchForm;
