import { Outlet } from "react-router-dom";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import logo from "../../images/Restify.jpg";


const SearchForm = () => {
    // create a state for query
    const [query, setQuery] = useState({ search: "", page_num: 1,
        city: "", country: "", num_of_guests: "", num_of_beds: "", property_type: "" });

    // create a state for properties
    const [properties, setProperties] = useState([]);
    const [filtersVisible, setFiltersVisible] = useState(false);

    const toggleFilters = () => {
        setFiltersVisible(!filtersVisible);
    }

    // fetch the properties from the backend
    useEffect(() => {
        const { 
            search, page_num,
            city, country, num_of_guests, num_of_beds, property_type
        } = query;
        fetch(`http://localhost:8000/property/list/?city=${city}&country=${country}&num_of_guests=${num_of_guests}&num_of_beds=${num_of_beds}&property_type=${property_type}&search=${search}&page=${page_num}`)
        .then(response => response.json())
        .then(data => {
            setProperties(data);
            console.log(query);
        });
    }, [query]);

    // make every three properties into a row
    const rows = [];
    for (let i = 0; i < properties.length; i += 3) {
        rows.push(properties.slice(i, i + 3));
    }

    const displayProperties = rows.map((row, index) => {

        var card_a = <div className="column is-one-quarter box is-hidden"> </div>;
        var card_b = <div className="column is-one-quarter box is-hidden"> </div>;
        var card_c = <div className="column is-one-quarter box is-hidden"> </div>;

        if (row?.[0]) {
            card_a = (
                <div className="column is-one-quarter box">
                    <Link>
                        <figure className="image is-5by4">
                            <img src={"http://127.0.0.1:8000/" + row?.[0].main_pic} alt="p_pic" style={{objectFit: "cover"}} />
                        </figure>
                    </Link>

                    <div>
                        <div>
                            {row?.[0].city}, {row[0].country}
                        </div>
                        <div>
                            {row?.[0].property_type}
                        </div>
                        <div>
                            {row?.[0].num_of_guests} guests
                        </div>
                        <div>
                            {row?.[0].lowest_avail_price}
                        </div>
                    </div>
                </div>
            );
        }

        if (row?.[1]) {
            card_b = (<>
                    
                <div className="column">
    
                </div> 
                <div className="column is-one-quarter box">
                    <Link>
                        <figure className="image is-5by4">
                            <img src={"http://127.0.0.1:8000/" + row?.[1].main_pic} alt="p_pic" style={{objectFit: "cover"}} />
                        </figure>
                    </Link>

                    <div>
                        <div>
                            {row?.[1].city}, {row[1].country}
                        </div>
                        <div>
                            {row?.[1].property_type}
                        </div>
                        <div>
                            {row?.[1].num_of_guests} guests
                        </div>
                        <div>
                            {row?.[1].lowest_avail_price}
                        </div>
                    </div>
                </div>
            </>
            );
        }

        if (row?.[2]) {
            card_c = (<>
                    
                <div className="column">
    
                </div> 
            
                    
                <div className="column is-one-quarter box">
                    <Link>
                        <figure className="image is-5by4">
                            <img src={"http://127.0.0.1:8000/" + row?.[2].main_pic} alt="p_pic" style={{objectFit: "cover"}} />
                        </figure>
                    </Link>

                    <div>
                        <div>
                            {row[2].city}, {row[2].country}
                        </div>
                        <div>
                            {row[2].property_type}
                        </div>
                        <div>
                            {row[2].num_of_guests} guests
                        </div>
                        <div>
                            {row[2].lowest_avail_price}
                        </div>
                    </div>
                </div>
            </>

            );
        }


                
        return (
            <>

            <div className="columns">
                
                    <div className="column">
        
                    </div>

                    {card_a}

                    {card_b}
                    
                    {card_c}
                    
                    <div className="column">
    
                    </div>
            </div>
            </>
        );
    });

    return (
        <>
                <section className="hero is-small">
                    <div className="hero-body has-text-centered">
                        <div className="container">
                            <text style={{fontSize: 100 + 'px', fontFamily: "fantasy", color: "purple"}} > Restify </text>
                        </div>
                    </div>
                </section>

                <section className="hero is-small">
                <div className="hero-body has-text-centered">
                    <div className="container">

                        <div className="columns">
                            <div className="column"></div>

                            <div className="column is-three-fifths-desktop is-full-mobile is-three-fifths-tablet">
                                <input type="text" className="input"
                                    placeholder="Search for a property"
                                    value={query.search}
                                    onChange={(e) => setQuery({...query, search: e.target.value, page_num: 1})}
                                />
                            </div>

                            <div className="column"></div>
                        </div>

                        
                        <div className="container">

                            <div className="columns">
                                <div className="column is-hidden-mobile"></div>

                                <div className="column is-one-fifth is-full-mobile is-one-fifth-tablet">
                                <div className="field has-addons">

                                    <div className="control">
                                    <button className="button is-link" onClick={toggleFilters}>
                                        {filtersVisible ? "Hide Filters" : "Show Filters"}
                                    </button>
                                    </div>

                                </div>
                                </div>

                                <div className="column is-hidden-mobile"></div>
                                <div className="column is-hidden-mobile"></div>
                                <div className="column is-hidden-mobile"></div>
                            </div>

                            {

                                filtersVisible &&
                                    
                                <>
                                    <div className="columns">

                                        <div className="column is-one-fifth"></div>

                                        <div className="column is-three-fifth-mobile">
                                            <input type="text" className="input"
                                            value={query.city}
                                            placeholder="City"
                                            onChange={(e) => setQuery({...query, city: e.target.value, page_num: 1})}
                                            />
                                        </div>

                                        <div className="column">
                                            <input type="text" className="input"
                                            value={query.country}
                                            placeholder="Country"
                                            onChange={(e) => setQuery({...query, country: e.target.value, page_num: 1})}
                                            />
                                        </div>

                                        <div className="column is-one-third"></div>

                                    </div>
                                    <div className="columns">

                                        <div className="column is-one-fifth"></div>
                                        
                                        <div className="column">
                                            <input type="text" className="input"
                                            value={query.num_of_guests}
                                            placeholder="Number of guests"
                                            onChange={(e) => setQuery({...query, num_of_guests: e.target.value, page_num: 1})}
                                            />
                                        </div>

                                        <div className="column">
                                            <input type="text" className="input"
                                            value={query.num_of_beds}
                                            placeholder="Number of beds"
                                            onChange={(e) => setQuery({...query, num_of_beds: e.target.value, page_num: 1})}
                                            />
                                        </div>

                                        <div className="column">
                                            <input type="text" className="input"
                                            value={query.property_type}
                                            placeholder="Property type"
                                            onChange={(e) => setQuery({...query, property_type: e.target.value, page_num: 1})}
                                            />
                                        </div>

                                        <div className="column is-one-third"></div>

                                    </div>
                                </>
                            }

                        </div>

                    </div>

                </div>
                </section>

                <section className="section">
                    <div className="container">
                            
                            {displayProperties}

                    </div>

                    <div className="container has-text-centered">
                        <div className="columns">
                                <div className="column is-one-third">

                                </div>

                                {
                                    query.page_num > 1 ?
                                    <div className="column">
                                    <button className="button is-link" onClick={() => setQuery({...query, page_num: query.page_num - 1})}> Previous </button>
                                    </div>
                                    :
                                    <div className="column">
                                    <button className="button is-link is-hidden" > Previous </button>
                                    </div>
                                }

                                <div className="column subtitle">
                                    Page {query.page_num}
                                </div>
                            
                                {
                                    <div className="column">
                                        <button className="button is-link" onClick={() => setQuery({...query, page_num: query.page_num + 1})}> Next </button>
                                    </div>
                                }
                                
                                <div className="column is-one-third">

                                </div>
                        </div>
                    </div>

                </section>
        <Outlet />
        </>
    );
}

export default SearchForm;
